INSERT INTO career (id, name, description) VALUES (1, 'Finance', 'Careers in finance involve managing money and investments.');
INSERT INTO career (id, name, description) VALUES (2, 'Engineering', 'Engineering is the application of science and math to solve problems.');
INSERT INTO career (id, name, description) VALUES (3, 'Technology', 'Technology careers involve working with computers and software.');
INSERT INTO career (id, name, description) VALUES (4, 'Agriculture', 'Agriculture is the science and art of cultivating plants and livestock.');
INSERT INTO career (id, name, description) VALUES (5, 'Real Estate', 'Real estate is property consisting of land and the buildings on it.');
INSERT INTO career (id, name, description) VALUES (6, 'Construction', 'Construction is the process of constructing a building or infrastructure.');
INSERT INTO career (id, name, description) VALUES (7, 'Leadership', 'Leadership is the action of leading a group of people or an organization.');
INSERT INTO career (id, name, description) VALUES (8, 'Politics', 'Politics is the set of activities that are associated with making decisions in groups, or other forms of power relations between individuals, such as the distribution of resources or status.');
INSERT INTO career (id, name, description) VALUES (9, 'Military', 'The military are the forces authorized to use deadly force, and weapons, to support the interests of the state and some or all of its citizens.');
INSERT INTO career (id, name, description) VALUES (10, 'Art', 'Art is a diverse range of human activity, and resulting product, that involves creative or imaginative talent expressive of technical proficiency, beauty, emotional power, or conceptual ideas.');
INSERT INTO career (id, name, description) VALUES (11, 'Design', 'Design is the creation of a plan or convention for the construction of an object or a system.');
INSERT INTO career (id, name, description) VALUES (12, 'Diplomacy', 'Diplomacy is the profession, activity, or skill of managing international relations.');
INSERT INTO career (id, name, description) VALUES (13, 'Entrepreneurship', 'Entrepreneurship is the creation or extraction of value.');
INSERT INTO career (id, name, description) VALUES (14, 'Entertainment', 'Entertainment is a form of activity that holds the attention and interest of an audience or gives pleasure and delight.');
INSERT INTO career (id, name, description) VALUES (15, 'Law', 'Law is a system of rules created and enforced through social or governmental institutions to regulate behavior.');
INSERT INTO career (id, name, description) VALUES (16, 'Research', 'Research is "creative and systematic work undertaken to increase the stock of knowledge".');
INSERT INTO career (id, name, description) VALUES (17, 'Philosophy', 'Philosophy is the study of general and fundamental questions, such as those about existence, knowledge, values, reason, mind, and language.');
INSERT INTO career (id, name, description) VALUES (18, 'Psychology', 'Psychology is the science of mind and behavior.');
INSERT INTO career (id, name, description) VALUES (19, 'Sales', 'Sales are activities related to selling or the number of goods or services sold in a given time period.');
INSERT INTO career (id, name, description) VALUES (20, 'Public Relations', 'Public relations is the practice of managing the spread of information between an individual or an organization and the public.');
INSERT INTO career (id, name, description) VALUES (21, 'Travel', 'Travel is the movement of people between distant geographical locations.');
INSERT INTO career (id, name, description) VALUES (22, 'Music', 'Music is the art of arranging sounds in time to produce a composition through the elements of melody, harmony, rhythm, and timbre.');
INSERT INTO career (id, name, description) VALUES (23, 'Social Work', 'Social work is an academic discipline and practice-based profession that concerns itself with individuals, families, groups, communities, and society as a whole in an effort to meet basic needs and enhance social functioning, self-determination, collective responsibility, and overall well-being.');
INSERT INTO career (id, name, description) VALUES (24, 'Science', 'Science is a systematic enterprise that builds and organizes knowledge in the form of testable explanations and predictions about the universe.');
INSERT INTO career (id, name, description) VALUES (25, 'Invention', 'An invention is a unique or novel device, method, composition or process.');
INSERT INTO career (id, name, description) VALUES (26, 'Comedy', 'Comedy is a genre of fiction consisting of discourses or works intended to be humorous or amusing by inducing laughter, especially in theatre, film, stand-up comedy, television, radio, books, or any other medium of entertainment.');
INSERT INTO career (id, name, description) VALUES (27, 'Management', 'Management is the administration of an organization, whether it is a business, a not-for-profit organization, or government body.');
INSERT INTO career (id, name, description) VALUES (28, 'Accounting', 'Accounting, also known as accountancy, is the measurement, processing, and communication of financial and non financial information about economic entities such as businesses and corporations.');
INSERT INTO career (id, name, description) VALUES (29, 'Journalism', 'Journalism is the production and distribution of reports on current events based on facts and supported with proof or evidence.');
INSERT INTO career (id, name, description) VALUES (30, 'Law Enforcement', 'Law enforcement is the activity of some members of government who act in an organized manner to enforce the law by discovering, deterring, rehabilitating, or punishing people who violate the rules and norms governing that society.');
INSERT INTO career (id, name, description) VALUES (31, 'Justice', 'Justice, in its broadest sense, is the principle that people receive that which they deserve, with the interpretation of what then constitutes "deserving" being impacted upon by numerous fields, with many differing viewpoints and perspectives, including the concepts of moral correctness based on ethics, rationality, law, religion, equity and fairness.');
INSERT INTO career (id, name, description) VALUES (32, 'Security', 'Security is freedom from, or resilience against, potential harm from external forces.');
INSERT INTO career (id, name, description) VALUES (33, 'Culinary Arts', 'Culinary arts are the cuisine arts of food preparation, cooking, and presentation of food, usually in the form of meals.');
INSERT INTO career (id, name, description) VALUES (34, 'Hospitality', 'The hospitality industry is a broad category of fields within the service industry that includes lodging, food and drink service, event planning, theme parks, travel and tourism.');
INSERT INTO career (id, name, description) VALUES (35, 'Philanthropy', 'Philanthropy is the desire to promote the welfare of others, expressed especially by the generous donation of money to good causes.');
